// You can reference the input variables using input.NAME
// Parsed JSON files could be referenced as fileInput
exports.step = function(input, fileInput) {

  var property_ids = []

  input.objects.forEach(object=>{
    object.fields.properties['en-US'].forEach(property=>{
      property_ids.push(property.sys.id)
    })
  })

	return property_ids
};

// You can reference the input variables using input.NAME
// Parsed JSON files could be referenced as fileInput
exports.step = function(input, fileInput) {
	return input.properties.map(prop=>{
    var obj = {}
    Object.keys(prop.fields).forEach(field=>{
      if(field != 'friendlyName'){
        obj[field] = prop.fields[field]['en-US']
      }
    })
    obj.id = prop.sys.id
    return obj
  })
};

// You can reference the input variables using input.NAME
// Parsed JSON files could be referenced as fileInput
exports.step = function(input, fileInput) {
  var result = {
    system: {
      id: input.system.sys.id,
      systemName:input.system.fields.systemName['en-US'],
      friendlyName:input.system.fields.friendlyName['en-US']
  	},
    objects: []
  }
  if (!input.system.fields.canonicalSystem) {
    result.canonicalSystem = false
  } else if (input.system.fields.canonicalSystem) {
    if (input.system.fields.canonicalSystem['en-US'] == 'TRUE') {
      result.canonicalSystem = true
    } else {
      result.canonicalSystem = false
    }
  }
  var objects = input.objects.map(object => {
    var properties = input.props.filter(prop => object.fields.properties['en-US'].map(prop => {
      return prop.sys.id
    }).includes(prop.id))
    return {
      friendlyName:object.fields.friendlyName['en-US'],
      systemName: object.fields.systemName['en-US'],
      id: object.sys.id,
      properties: properties
    }
  })
  result.objects = objects

	return result;
};
